// common

* {
  list-style: none;
  text-decoration: none;
}

// 눈송이 효과
.snowflake {
  width: 8px;
  height: 8px;
  border-radius: 50%;
  background-color: white;
  position: absolute;
  top: -8px;
}

@keyframes fall {
  from {
  }
  to {
    transform: translateY(100vh);
    opacity: 0;
  }
}

@keyframes showMenu {
  from {
    left: -600px;
  }
  to {
    left: 100px;
  }
}

@keyframes hideMenu {
  from {
    left: 100px;
  }
  to {
    left: -600px;
  }
}

// body

body {
  height: 100vh;
  box-sizing: border-box;
  background-repeat: no-repeat;
  background-size: cover;
  background-position: center;
  font-family: "Montserrat", sans-serif;

  // header
  header {
    position: relative;
    width: 100wh;
    height: 10vh;
    display: flex;
    align-items: center;

    .menuBtn {
      display: flex;
      justify-content: center;
      align-items: center;
      margin-left: 10px;
      margin-right: 30px;
      padding: 0 5px;
      height: 50%;
      background-color: royalblue;
      color: whitesmoke;
      border-radius: 50%;
      cursor: pointer;
    }
    .menu-bar {
      position: absolute;
      left: -600px;
      display: flex;
      justify-content: center;
      align-items: center;
      border-bottom: 2px solid white;
      .utils {
        ul {
          display: flex;
          li {
            background-color: rgba($color: white, $alpha: 0.1);
            border-radius: 30px;
            cursor: pointer;
            margin-right: 10px;
            &:nth-child(1) {
              a {
                color: rosybrown;
              }
            }
            &:nth-child(2) {
              a {
                color: royalblue;
              }
            }
            &:nth-child(3) {
              a {
                color: tomato;
              }
            }
          }
        }
        a {
          display: flex;
          justify-content: center;
          align-items: center;
          width: 50px;
          height: 30px;
          box-shadow: 1px 1px 5px white;
          border-radius: 30px;
        }
      }

      .snowflakeBtn {
        width: 60px;
        height: 30px;
        background-color: rgb(73, 68, 68);
        border-radius: 40px;
        cursor: pointer;
        span {
          cursor: pointer;
          margin: auto 0;
          transition: all 0.5s linear;
          display: block;
          width: 30px;
          height: 30px;
          border-radius: 50%;
          background-color: rgb(237, 231, 231);
        }
        &.active {
          background-color: rgb(237, 231, 231);
          span {
            background-color: rgb(35, 27, 27);
            transform: translateX(30px);
          }
        }
      }
      .modeBtn {
        margin-left: 20px;
        margin-right: 50px;
        display: flex;
        justify-content: center;
        align-items: center;
        z-index: 1;
        background-color: black;
        color: white;
        width: 120px;
        height: 30px;
        border-radius: 50px;
        cursor: pointer;
      }
    }

    .profile {
      position: absolute;
      top: auto;
      right: 10px;
      background-color: royalblue;
      padding: 12px 10px;
      border-radius: 50%;
      color: white;
      cursor: pointer;
    }
  }

  //main
}

